function formatPhone(t){let e=t.value.replace(/\D/g,"");e.length>=3&&(e="8 ("+e.substr(1,3)+") "+e.substr(4,3)+"-"+e.substr(7,2)+"-"+e.substr(9,2),t.value=e)}const phoneInputs=document.querySelectorAll('input[type="tel"]');phoneInputs.forEach((t=>{t.addEventListener("input",(()=>{formatPhone(t)}))}));
